class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ret = 0;
        int n = nums.length;
        int j = 0;
        int p = 0;
        for (int i = 0; i < n; i++) {
            int a = target[i] - nums[i];
            if (a == 0) {
                p = 0;
                continue;
            } 
            if (p == 0) {
                ret += Math.abs(a);
            } else {
                if (a > 0 && p > 0) {
                    if (p < a) {
                        ret += a - p;
                    }
                } else if (a < 0 && p < 0) {
                    if (p > a) {
                        ret += p - a;
                    }
                } else {
                    ret += Math.abs(a);
                }
            }
            p = a;
        }
        return ret;
    }
}
