class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int flag=0;
        for(int i=0;i<nums.size();)
        {
            if(nums[i]<target[i])
                flag=1;
            else if(nums[i]>target[i])
                flag=-1;
            else
            {
                i++;
                continue;
            }
            int j;
            for(j=i+1;j<nums.size();j++)
            {
                if((flag==1 && nums[j]<target[j])||(flag==-1 && nums[j]>target[j]))
                    continue;
                else
                    break;
            }
            int mind=abs(nums[i]-target[i]);
            for(int k=i;k<j;k++)
            {
                if(flag==1)
                    mind=min(mind,target[k]-nums[k]);
                else
                    mind=min(mind,nums[k]-target[k]);
            }
            for(int k=i;k<j;k++)
            {
                if(flag==1)
                    nums[k]+=mind;
                else 
                    nums[k]-=mind;
            }
            ans+=mind;
        }
        return ans;
    }

};
