class Solution {
public:

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<vector<int>>vv;
        int n=nums.size();
        vector<int>temp;
        vector<int>v;
        for(int i=0;i<n;i++){
               v.push_back(nums[i]-target[i]);
        }

        int sign=(v[0]>0);
        for(int i=0;i<n;i++){
            int newsign=(v[i]>0);
            if(newsign==sign){
                temp.push_back(abs(v[i]));
            }
            else{
                sign=1-sign;
                vv.push_back(temp);
                temp.clear();
                temp.push_back(abs(v[i]));
            }
        }
        vv.push_back(temp);
        long long ans=0;
        for(auto it:vv){
            int prev=0;
           for(auto jt:it){
            if(jt<prev);
            else{
              ans+=jt-prev;
            }
            prev=jt;
           }
        }

        return ans;
    }
};
