class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> cf(nums.size(), 0);
        long long ans = 0;
        cf[0] = target[0] - nums[0];
        for (int i = 1; i < nums.size(); i++) {
            cf[i] = target[i]-nums[i] - target[i-1]+nums[i-1];
        }
        long long cfp = 0;
        long long cfn = 0;
        for (int i = 0; i < cf.size(); i++) {
            if (cf[i] < 0) cfn -= cf[i];
            if (cf[i] > 0) cfp += cf[i];
            // printf("cf[%d]=%lld\n", i, cf[i]);
        }
        return max(cfn, cfp) - min(cfn, cfp) + min(cfn, cfp);
    }
};
