class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        a = [i-j for i, j in zip(target, nums)]
        cur, add, minus = 0, 0, 0
        ret = 0
        for x in a:
            if cur < x:
                t = x - cur
                if t <= minus:
                    minus -= t
                else:
                    add += t-minus
                    ret += t-minus
                    minus = 0
            elif cur > x:
                t = cur - x
                if t <= add:
                    add -= t
                else:
                    minus += t-add
                    ret += t-add
                    add = 0
            cur = x
            # print(cur, add, minus)
        return ret
