class Solution {

    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] diff = new long[n];
        for(int i = 0; i < n; i++){
            long d = target[i] - nums[i];
            diff[i] = d;
        }

        long result = Math.max(diff[0], -diff[0]);
        for(int i = 1; i < n; i++){
            if(diff[i] < 0){
                if(diff[i] >= diff[i - 1]) continue;
                result += Math.min(-diff[i], diff[i - 1] - diff[i]);
            }else{
                if(diff[i] <= diff[i - 1]) continue;
                result += Math.min(diff[i], diff[i] - diff[i - 1]);
            }
        }

        return result;
    }
}
