class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long res = 0;
        vector<int> diff;
        for(int i=0; i<n; i++)
            diff.push_back(target[i]-nums[i]);
        int i=0, j=0;
        int s = 1;
        if(diff[0]<0) s = -1;
        while(i<n)
        {
            if(diff[i]==0)
            {
                i++;
                continue;
            }
            if(diff[i]>0)
            {
                int prevDif = 0;
                // priority_queue<int, vector<int>, greater<int>> p;
                while(i<n && diff[i]>0)
                {
                    if(diff[i]>prevDif)
                    {
                        res += abs(diff[i]-prevDif);
                        prevDif = diff[i];
                    }
                    else
                    {
                        prevDif = diff[i];
                    }
                    i++;
                }
            }
            else
            {
                int prevDif = 0;
                while(i<n && diff[i]<0)
                {
                    if(diff[i]<prevDif)
                    {
                        res += abs(diff[i]-prevDif);
                        prevDif = diff[i];
                    }
                    else
                    {
                        prevDif = diff[i];
                    }
                    i++;
                }
            }
        }
        return res;
    }
};
