using namespace std;
#include<bits/stdc++.h>
#define ll long long
#define pb push_back
#define all(x) x.begin(), x.end()
#define float long double
#define double long double
#define sortall(x) sort(all(x))
#define sz(a) (int)(a).size()
const int mod = 1e9+7;
//<--------------------------------------------------------------------------------->

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = sz(nums);
        vector<int> mini(n,0);
        ll ans = 0;

        for(int i=0;i<n;i++){
            mini[i] = target[i] - nums[i]; 
        }
        for(int i=0;i<n;i++){
            if(i==0){
                ans+=abs(mini[i]);
            }
            else {
                int fir = ((mini[i-1]>=0)?1:0);
                int sec = ((mini[i]>=0)?1:0);

                if(fir && sec){
                    if(mini[i-1]>=mini[i]){

                    }
                    else {
                        ans += (mini[i]-mini[i-1]);
                    }
                }
                else if(!fir && !sec){
                    // cout<<mini[i-1]<<" "<<mini[i]<<" ";
                    if(mini[i-1]<=mini[i]){

                    }
                    else{
                        ans += abs(mini[i-1] - mini[i]);
                    }
                }
                else{
                    ans+=abs(mini[i]);
                }
            }
            // cout<<ans<<" ";
        }
        return ans;
    }
};
