class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [(val[1] - val[0]) for val in zip(nums, target)]

        ans = 0
        for gr in groupby(diff, lambda x: (-1 if x < 0 else (1 if x > 0 else 0))):
            a = list(gr[1])
            if a[0] == 0:
                continue
            ans += self.subSolve(a)

        return ans

    def subSolve(self, a):
        for i in range(len(a)):
            a[i] = abs(a[i])
        ans = 0
        st = [0]

        a.append(0)

        for x in a:
            if x == st[-1]:
                continue
            elif x > st[-1]:
                st.append(x)
                continue

            while st[-1] > x:
                ans += st[-1] - max(x, st[-2])
                # print(f'updating: {x=} {st=} {ans=}')
                st.pop()
            st.append(x)

        return ans
