class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]

        ans = i = 0

        while i < n:
            while diff[i] != 0:
                j = i
                min_diff = abs(diff[i])
                while j < n and diff[j] * diff[i] > 0:
                    min_diff = min(abs(diff[j]), min_diff)
                    j += 1

                ans += min_diff
                for k in range(i, j):
                    diff[k] += min_diff if diff[k] < 0 else -min_diff

            i += 1

        return ans
