class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        for i in range(len(nums)):
            nums[i] -= target[i]
        ans = 0
        i = 0
        while i < len(nums):
            if nums[i] < 0:
                ans += -nums[i]
                i += 1
                while i < len(nums) and nums[i] <= 0:
                    ans += max(nums[i - 1] - nums[i], 0)
                    i += 1
            else:
                ans += nums[i]
                i += 1
                while i < len(nums) and nums[i] >= 0:
                    ans += max(nums[i] - nums[i - 1], 0)
                    i += 1
        return ans
