class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
     int n = nums.size();
        vector<int> arr(n);


        for (int i = 0; i < n; ++i) {
            arr[i] = target[i] - nums[i];
        }

        long long count = 0;
        int res1 = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (arr[i - 1] > 0 && arr[i] > 0) || (arr[i - 1] < 0 && arr[i] < 0)) {
                int pr = std::abs(arr[i]) - std::abs(res1);
                if (pr > 0) {
                    count += pr;
                }
                res1 = arr[i];
            } else {
                count += std::abs(arr[i]);
                res1 = arr[i];
            }
        }

        return count;
    }
};
