class Solution {
    fun minimumOperations(nums: IntArray, target: IntArray): Long {
        var res = 0L
        var prev= target[0]-nums[0]
        res += abs(prev)
        for(i in 1 until nums.size){
            val curr = target[i]-nums[i]
            if(!sign(prev, curr)){
                res += abs(curr)
                prev = curr
                continue
            }
            if(abs(prev) < abs(curr)) {
                res += abs(curr)-abs(prev)
            }
            prev = curr
        }
        return res
    }

    fun abs(n:Int):Int {
        return if(n < 0) -n else n
    }

    fun sign(a:Int, b:Int):Boolean {
        if(a == 0 || b == 0) return false
        if(a < 0 && b < 0) return true
        if(a > 0 && b > 0) return true
        return false
    }
}
