class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [x-y for x,y in zip(nums,target)]
        ans = abs(diffs[0])+abs(diffs[-1])
        for i in range(1,len(diffs)):
            ans += abs(diffs[i]-diffs[i-1])
        return ans//2
