class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long nop=0,inc=0,dec=0;

        for(int i=0;i<nums.length;i++)
        {
            if(nums[i]<target[i])
            {
                dec=0;
                 if(inc<(target[i]-nums[i]))
                 {
                    nop += ((target[i]-nums[i])-(long)inc);
                    inc = (long)target[i]-nums[i];

                 }
                 else
                 {
                    inc = (long)target[i]-nums[i];
                 }
            }
            else if(nums[i]>target[i])
            {
                inc=0;
                if(dec<(nums[i]-target[i]))
                 {
                    nop += ((nums[i]-target[i])-(long)dec);
                    dec =  (long)nums[i]-target[i];

                 }
                 else
                 {
                     dec = (long)nums[i]-target[i];
                 }

            }
            else
            {
                dec=0L;
                inc=0L;
            }
        }
        return nop;
    }
}
