class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        int diff=0,maxi=0;
        bool x=false; 
        int i=0;
        while(i<n)
        {
            if(nums[i]-target[i]>0)
            {
                while(i<n && nums[i]>target[i])
                {
                    if(nums[i]-target[i]>maxi)
                    {
                        ans+=(nums[i]-target[i]-maxi);
                    }
                    maxi=nums[i]-target[i];
                    i++;
                }
                maxi=0;
            }
            else if(nums[i]<target[i]){
                while(i<n && nums[i]<target[i])
                {
                    if(target[i]-nums[i]>maxi)
                    {
                        ans+=(target[i]-nums[i]-maxi);
                    }
                    maxi=target[i]-nums[i];
                    i++;
                }
                maxi=0;
            }
            else
            {
                i++;
            }
        }
        return ans;
    }
};
