class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0, pre = 0;
        //+ + a < b  ans += (b-a);
        //+ + a >= b  ans += 0;
        //- - a < b ans += 0;
        //- - a >= b ans += (b-a);
        //+ -
        //- +
        for(int i=0;i<n;i++){
            long long d = target[i]-nums[i];
            if((d > 0 && pre > 0) || (d < 0 && pre < 0)){//同号
                if(abs(pre) < abs(d)) ans += (abs(d)-abs(pre))*1LL;
            }else{
                ans += abs(d)*1LL;
            }
            // cout << pre << " " << d << " " << ans << endl;
            pre = d;
        }
        return ans;
    }
};
