class Solution {
public:
    long long solve(int prev, int ind, vector<int>& delta) {
        if(ind < prev) return 0;
        stack<int> c;
        long long cnt = 0;
        for(int i = prev ; i <= ind ; i++) {
            int res = 0;
            while(!c.empty() && c.top() > abs(delta[i])) {
                res = max(c.top() - abs(delta[i]), res);
                c.pop();
            }
            cnt += res;
            if(c.empty()) c.push(abs(delta[i]));
            else if(c.top() != abs(delta[i])) c.push(abs(delta[i]));
        }
        cnt += abs(c.top());
        return cnt;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> delta;
        int n = nums.size();
        for(int i = 0 ; i < nums.size() ; i++) {
            delta.push_back(target[i] - nums[i]);
        }
        long long ans = 0;
        int f = 0;
        int prev = 0;
        for(int i = 0; i < nums.size() ; i++) {
            if(f == 0) {
                if(delta[i] > 0) f = 1;
                else if(delta[i] < 0) f = -1;
            }
            if(delta[i] == 0 && f != 0) {
                ans += solve(prev, i-1, delta);
                f = 0;
                prev = i;
            } else if(f < 0 && delta[i] > 0) {
                ans += solve(prev, i-1, delta);
                f = 1;
                prev = i;
            } else if(f > 0 && delta[i] < 0) {
                ans += solve(prev, i-1, delta);
                f = -1;
                prev = i;
            }
        }
        ans += solve(prev, n-1, delta);
        return ans;
    }
};
