class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        for (int i = 0; i < nums.length; i++) nums[i] -= target[i];
        long res = Math.abs(nums[0]);
        int sign = Integer.signum(nums[0]);
        for (int i = 1; i < nums.length; i++) {
            int newSign = Integer.signum(nums[i]);
            int num = Math.abs(nums[i]);
            if (sign != newSign) {
                res += num;
            } else {
                int prev = Math.abs(nums[i-1]); 
                if (num > prev) {
                    res += num - prev; 
                }
            }
            sign = newSign;
        }
        return res;
    }


}
