class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        int n=nums.size();
        for(int i=0;i<n;i++){
            diff.push_back(target[i]-nums[i]);
        }
        long long ans=0;
        for(int i=0;i<n;i++){
            if(diff[i]>0){
                int j=i;
                int mi=INT_MAX;
                while(j<n &&diff[j]>0){
                    mi=min(mi,diff[j]);
                    j++;
                }
               //cout<<i<<j<<endl;
                ans+=mi;
                //cout<<ans<<endl;
                for(int k=i;k<j;k++){
                    diff[k]-=mi;
                }

                i--;
            }
            else if(diff[i]<0){
                int j=i;
                int mi=INT_MIN;
                while(j<n && diff[j]<0){
                    mi=max(mi,diff[j]);
                    j++;
                }
                ans+=abs(mi);
                for(int k=i;k<j;k++){
                    diff[k]-=mi;
                }
                i--;
            }
        }
        return ans;

    }
};
