class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(nums)
        dp=[0,0]
        for i in range(n):
            d=target[i]-nums[i]
            if target[i]==nums[i]:
                dp[1]=0
            elif target[i]>nums[i]:
                if dp[1]>0:
                    if d>=dp[1]:
                        dp[0]+=d-dp[1]
                        dp[1]=d
                    else:
                        dp[1]=d
                else:
                    dp[0]+=d
                    dp[1]=d
            else:
                if dp[1]<0:
                    if d<=dp[1]:
                        dp[0]+=dp[1]-d
                        dp[1]=d
                    else:
                        dp[1]=d
                else:
                    dp[0]+=-d
                    dp[1]=d
        return dp[0]
