class Solution {
public:
    long long minimumOperations(vector<int> nums, vector<int>target) {
        int n = nums.size();
        vector<int> d(n);
        for (int i = 0; i < n; ++i) {
            d[i] = target[i] - nums[i];
        }

        long long  ops = 0;
        long long csd = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)) {
                int addOps = abs(d[i]) - abs(csd);
                if (addOps > 0) {
                    ops += addOps;
                }
                csd = d[i];
            } else {
                ops += abs(d[i]);
                csd = d[i];
            }
        }

        return ops;
    }
};
