class Solution {
public:
    using ll=long long;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size();
         string prev="zero";
         ll prevcnt=0;
         ll ans=0;
        for(ll i=0;i<n;i++)
        {
            ll cost=abs(nums[i]-target[i]);
            if(nums[i]<target[i])
            {  
                if(prev=="right")
                {
                    if(cost>prevcnt)
                    {
                        ans+=(cost-prevcnt);
                    }

                }else
                {
                    prevcnt=0;
                    if(cost>prevcnt)
                    {
                        ans+=(cost-prevcnt);
                    }

                }
                 prev="right";
            }else
            {
                if(prev=="left")
                {
                    if(cost>prevcnt)
                    {
                        ans+=(cost-prevcnt);
                    }

                }else
                {
                    prevcnt=0;
                    if(cost>prevcnt)
                    {
                        ans+=(cost-prevcnt);
                    }

                }

                prev="left";
            }
            prevcnt=cost;
        }
        return ans;
    }
};
