class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        vector<int>arr(n);
        for(int i=0;i<n;++i) arr[i]=target[i]-nums[i];
        arr.push_back(0);
        ans=max(0LL,(long long)arr[0]);
        for(int i=1;i<=n;++i) {
            if(arr[i]>arr[i-1]) ans+=arr[i]-arr[i-1];
        }
        return ans;
    }
};
