class Solution {
public:
    long long f(vector<long long>& v) {
        //cout << v.size() << endl;
        if(v[0] > 0) {
            long long ans = v[0];
            for(int i = 1; i < v.size(); i ++) {
                if(v[i] > v[i-1])     ans += v[i] - v[i-1];
            }
            return ans;
        }
        else {
            long long ans = abs(v[0]);
            for(int i = 1; i < v.size(); i ++) {
                if(v[i] < v[i-1])     ans += abs(v[i] - v[i-1]);
            }   
            return ans;
        }
        return 0;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        for(int i = 0; i < nums.size(); ) {
            if(nums[i] == target[i]) {
                i ++; 
                continue;
            }
            int diff =  (- nums[i] + target[i]) > 0 ? 1 : 0;

            int j = i;
            vector<long long> v;
            if(diff == 1) {
                while(j < nums.size() and target[j] > nums[j]) {
                    v.push_back((long long) target[j] - (long long) nums[j]);
                    j ++;
                }
            }
            else {
                while(j < nums.size() and target[j] < nums[j]) {
                    v.push_back((long long) target[j] - (long long) nums[j]);
                    j ++;
                }
            }
            //cout << i << " "<< j << endl;
            ans += f(v);
            i = j;
        }
        return ans;
    }
};
