class Solution {
public:
long long rec(int diff, int& posii, long long& minii, long long tots) {
        if (diff < 0 && posii == 1) {
            tots += abs(diff);
            minii = abs(diff);
            posii = 0;
        } else if (diff >= 0 && posii == 0) {
            tots += abs(diff);
            minii = abs(diff);
            posii = 1;
        } else if (minii >= abs(diff)) {
            minii = abs(diff);
        } else {
            tots += abs(diff) - minii;
            minii = abs(diff);
        }

        return tots;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
       vector<int>subs(n);
       for(int i=0; i<n; i++){
        subs[i]=nums[i]-target[i];
       }

        int posii = 0;
        long long minii = 0, tots = 0;

        tots = abs(subs[0]);
        minii = tots;
        cout<<minii<<" "<<tots<<endl;

        if (subs[0] >= 0) {
            posii = 1;
        }

        for (int i = 1; i < n; ++i) {
             cout<<minii<<" "<<tots<<endl;
            tots = rec(subs[i], posii, minii, tots);
        }

        return tots;
    }



};
