class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> v(n);

        for (int i = 0; i < n; ++i) {
            v[i] = target[i] - nums[i];
        }

        long long res = 0;
        long long curr = 0;
        int i = 0;

        while (i < n) {
            if (i == 0 || (v[i - 1] > 0 && v[i] > 0) || (v[i - 1] < 0 && v[i] < 0)) {
                long long add = abs(v[i]) - abs(curr);
                if (add > 0){
                    res += add;
                }
                curr = v[i];
            } else {
                res += abs(v[i]);
                curr = v[i];
            }
            ++i;
        }

        return res;
    }
};
