class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans=0;
        vector<long long> v(n,0);
        for(int i=0;i<n;i++){
            v[i] = target[i]-nums[i];
        }
        ans = abs(v[0]);
        for(int i=1;i<n;i++){
            if(v[i]*v[i-1]<0){
                // 符号相反
                ans += abs(v[i]);
            }else{
                // 符号相同
                if(abs(v[i]) > abs(v[i-1])){
                    ans += (abs(v[i])-abs(v[i-1]));
                }
            }
        }
        return ans;

    }
};
