class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def sign(v):
            if v > 0:
                return 1
            elif v < 0:
                return -1
            else:
                return 0

        def fun(A):
            if A[0] == 0:
                return 0
            if A[0] < 0:
                A = [-v for v in A]
            # print(A)
            ans = A[0] 
            for i in range(1, len(A)): 
                if A[i] > A[i-1]:
                    ans += A[i]-A[i-1] 
            return ans 

        tmp = [a - b for a, b in zip(target, nums)]
        dif = [d for d, _ in groupby(tmp)]

        ans = 0
        gg = []
        tmp = []
        for v in dif:
            if not tmp:
                tmp.append(v)
                continue
            else:
                if sign(tmp[-1]) == sign(v):
                    tmp.append(v)
                else:
                    gg.append(tmp)
                    tmp = [v]
        gg.append(tmp)
        # print(gg)
        return sum(fun(g) for g in gg)
