class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] diff = new long[n];
        for (int i = 0; i < n; i++) {
            diff[i] = (long)target[i] - (long)nums[i];
        }

        long totalOperations = 0;
        long currentSegmentDiff = 0;

        for (int i = 0; i < n; i++) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long additionalOperations = Math.abs(diff[i]) - Math.abs(currentSegmentDiff);
                if (additionalOperations > 0) {
                    totalOperations += additionalOperations;
                }
                currentSegmentDiff = diff[i];
            } else {
                totalOperations += Math.abs(diff[i]);
                currentSegmentDiff = diff[i];
            }
        }

        return totalOperations;
    }
}
