class Solution {
public:
  long long minimumOperations(vector<int>& nums, vector<int>& target) {
    for (int i = 0; i < nums.size(); ++i) {
      nums[i] -= target[i];
    }
    long long result = 0;
    result += abs(nums[0]);
    for (int i = 1; i < nums.size(); ++i) {
      if ((nums[i] ^ nums[i-1]) < 0) {
        result += abs(nums[i]);
        continue;
      }
      if (abs(nums[i]) > abs(nums[i-1])) {
        result += abs(nums[i]) - abs(nums[i-1]);
      }
    }
    return result;
  }
};
