using ll = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> arr;
        ll ans = 0;
        for (int i = 0; i < nums.size(); ++i) {
            int x = target[i] - nums[i];
            if (x < 0) {
                if (!arr.empty() && arr.back() > 0) {
                    arr.clear();
                    ans += -x;
                }
                else {
                    bool flag = false;
                    while (!arr.empty() && arr.back() <= x) {
                        arr.pop_back();
                        flag = true;
                    }
                    if (!flag) {
                        if (!arr.empty()) {
                            ans += arr.back() - x;
                        }
                        else {
                            ans += -x;
                        }
                    }
                }
                arr.push_back(x);

            }
            else if (x > 0) {
                if (!arr.empty() && arr.back() < 0) {
                    arr.clear();
                    ans += x;
                }
                else {
                    bool flag = false;
                    while (!arr.empty() && arr.back() >= x) {
                        arr.pop_back();
                        flag = true;
                    }
                    if (!flag) {
                        if (!arr.empty()) {
                            ans += x - arr.back();
                        }
                        else {
                            ans += x;
                        }
                    }
                }
                arr.push_back(x);
            }
            else {
                arr.clear();
            }
        }
        return ans;
    }
};
