/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
  const n = nums.length;
  const diff = target.map((x, i) => x - nums[i]);
  // console.log({diff})
  let res = 0, mode = 0, last = 0;
  for (let i = 0; i < n; i++) {
    if (mode > 0) {
      if (diff[i] <= 0) {
        mode = diff[i];
        last = diff[i];
        res -= diff[i];
      } else {
        if (diff[i] > last) {
          res += diff[i] - last;
          last = diff[i];
        } else if (diff[i] < last) {
          last = diff[i];
        }
      }
    } else if (mode < 0) {
      if (diff[i] >= 0) {
        mode = diff[i];
        last = diff[i];
        res += diff[i];
      } else {
        if (diff[i] < last) {
          res += last - diff[i];
          last = diff[i];
        } else if (diff[i] > last) {
          last = diff[i];
        } 
      }
    } else {
      mode = diff[i];
      last = diff[i];
      res += Math.abs(diff[i]);
    }
    // console.log({i, mode, last, res})
  }
  return res;
};
