class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans=0;
        long long d= target[0]-nums[0];
        ans+=abs(d);
        int p=-1;
        for(int i=1;i<n;i++){
            long long l = target[i]-nums[i];
            if(l>0 && d>0){
                if(l>d){
                    ans+=(l-d);
                    d=l;
                }
                else{
                    d=l;
                }
            }
            else if(l<0 && d<0){
                if(l>d){
                    d=l;
                }
                else{
                    ans+=(d-l);
                    d=l;
                }
            }
            else if(l==0){
                d=0;
            }
            else{
                ans+=abs(l);
                d=l;
            }
            // cout<<ans<<" "<<d<<" p ";
        }

        return ans;
    }
};
