class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
		long long ans = 0;
		//num ->    [1,3,2]
		//target -> [2,1,4]
		int mostRecentChange = 0;
		for(int i = 0 ; i < nums.size(); ++i){
			int neededChange = target[i]-nums[i];
			if(neededChange < 0){
				if(mostRecentChange <= neededChange){
					mostRecentChange = neededChange;
					continue;
				}
				else{
					ans += abs(neededChange-(min(0, mostRecentChange)));
					mostRecentChange = neededChange;
					continue;
				}
			}
			else{
				if(mostRecentChange >= neededChange){
					mostRecentChange = neededChange;
					continue;
				}
				else{
					ans += neededChange-(max(mostRecentChange, 0));
					mostRecentChange = neededChange;
					continue;
				}
			}
		}
		return ans;
	}
};
