class Solution {
public:
    long long f(vector<int>& target) {
        long long ans = target[0];
        for(int i =1;i<target.size();i++){
            if(target[i]-target[i-1]>0){
                ans+= (target[i]-target[i-1]);
            }
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++){
            diff[i] = target[i]-nums[i];
        }

        long long ans = 0;
        for(int i=0;i<n;i++){
            if(diff[i]==0) continue;
            if(diff[i]>0){
                vector<int> temp;
                int j = i;
                while(j<n && diff[j]>0){
                    temp.push_back(diff[j]);
                    j++;
                }
                i = j-1;
                ans += f(temp);
            }
            else{
                vector<int> temp;
                int j = i;
                while(j<n && diff[j]<0){
                    temp.push_back(-diff[j]);
                    j++;
                }
                i = j-1;
                ans += f(temp);
            }
        }
        return ans;
    }
};
