class Solution {
    public long minimumOperations(int[] num, int[] t) {
        int n = num.length;
        long [] a = new long[n];
        for(int i = 0;i<n;i++){
            a[i] = t[i]-num[i];
        }
        long ans=0;
        long max = Math.abs(a[0]);
        // System.out.println(Arrays.toString(a));
        for(int i = 1;i<n;i++){
            if(a[i]<0 && a[i-1]>=0){
                ans+=max;
                max = Math.abs(a[i]);
            }else if(a[i]>0 && a[i-1]<=0){
                ans+=max;
                max = a[i];
            }else{
                if(max>0 && Math.abs(a[i-1])>Math.abs(a[i])){
                    ans+=Math.abs(a[i-1])-Math.abs(a[i]);
                    max = Math.abs(a[i]);
                }else if(max>0 && Math.abs(a[i-1])<Math.abs(a[i])){
                    max = Math.abs(a[i]);
                }
            }
        }
        ans+=max;
        return ans;
    }
}
