class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size(), A[n];
        for (int i=0; i<n; i++)
            A[i] = target[i] - nums[i];
        int i = 0, off = 0;
        long long res = 0;
        while (i < n) {
            if (A[i] >= 0) {
                while (i+1<n && A[i+1] >= A[i])
                    i++;
                res += A[i++] - off;
                if (i<n && A[i] > 0)
                    off = A[i];
                else
                    off = 0;
            }
            else {
                while (i+1<n && A[i+1] <= A[i])
                    i++;
                res += off - A[i++];
                if (i<n && A[i] < 0)
                    off = A[i];
                else
                    off = 0;
            }
        }
        return res;
    }
};
