class Solution {
public:
    long long sol(int l,int r,vector<int>& a,int f){
        long long ans=f*a[l];
        for (int i = l+1; i < r; ++i)
            ans += max(f*a[i] - f*a[i - 1], 0);
        return ans;
    }
    int g(int x){
        if(x>=0) return 1;
        else return -1;
    }
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        for (int i = 0; i < n; i++)
            a[i] = b[i] - a[i];
        int l=0,r=0;
        long long ans = 0;
        for (int i = 1; i < n; ++i) {
            if ((a[i] >= 0 && a[i - 1] < 0) || (a[i] < 0 && a[i - 1] >= 0)) {
                ans+=sol(l,i,a,g(a[i-1]));
                l=i;
            }
        }
        ans+=sol(l,n,a,g(a[n-1]));
        return ans;
    }
};
