class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int mind=INT_MAX, mindi=-1;
        vector<int>diff(n,0);
        for(int i=0;i<n;i++){
            diff[i]=nums[i]-target[i];

        }
        long long  ans=1ll*abs(diff[0]);
        for(int i=1;i<n;i++){
            if(diff[i]>0 && diff[i-1]<diff[i]){
                ans+=1ll*(min(abs(diff[i]-diff[i-1]),diff[i]));
            }else if(diff[i]<0 && diff[i]<diff[i-1]){
                ans+=1ll*(min(abs(diff[i]-diff[i-1]),abs(diff[i])));
            }
        }
        return ans;
    }
};
