class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> d(n);
        for (int i = 0; i < n; i++) d[i] = target[i] - nums[i];
        long long ans = 0, c = 0;
        for (int i = 0; i < n; i++) {
            int v = 0;
            if (d[i] * 1LL * c >= 0) {
                if (abs(d[i]) < abs(c)) v = 0;
                else v = d[i] - c;
            }
            else v = d[i];
            ans += abs(v);
            c = d[i];
        }
        return ans;
    }
};
