class Solution {
    public static void main(String[] args) {
        Solution solution = new Solution();
        solution.minimumOperations(new int[]{1,1,3,4}, new int[]{4,1,3,2});
    }
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] diff = new long[n];
        for (int i = 0; i < n; i++) {
            diff[i] = nums[i] - target[i];
        }
        long[] dp = new long[n];
        dp[0] = Math.abs(diff[0]);
        for (int i = 1; i < n; i++) {
            if (diff[i] >= 0) {
                if (diff[i - 1] * diff[i] <= 0) {
                    dp[i] = dp[i - 1] + diff[i];
                    continue;
                }
                if (diff[i - 1] >= diff[i]) {
                    dp[i] = dp[i - 1];
                }else{
                    dp[i] = dp[i - 1] + diff[i] - diff[i - 1];
                }
            }else{
                if (diff[i - 1] * diff[i] <= 0) {
                    dp[i] = dp[i - 1] - diff[i];
                    continue;
                }
                if (diff[i - 1] <= diff[i]) {
                    dp[i] = dp[i - 1];
                }else{
                    dp[i] = diff[i - 1] - diff[i] + dp[i - 1];
                }
            }
        }
        return dp[n - 1];
    }
}
