class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        d = [nums[i] - target[i] for i in range(n)]
        ans = 0
        cur_op = 0
        ans += abs(cur_op)
        for i in range(n):
            if cur_op==0:
                ans += abs(d[i])
            elif cur_op * d[i] > 0:
                if abs(cur_op) <  abs(d[i]):
                    ans += abs(d[i] - cur_op)
            else:
                ans+= abs(d[i])
            cur_op = d[i]

        return ans
