class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
          vector<long long> mx;
        for(int i=0;i<nums.size();i++){
            mx.push_back(target[i]-nums[i]);
        }
        long long ans=abs(mx[0]);
        for(int i=1;i<mx.size();i++){
            if(mx[i-1]*mx[i]>0){
                if(abs(mx[i])>=abs(mx[i-1]))
                    ans+=abs(mx[i])-abs(mx[i-1]);

            }
            else {
                ans+=abs(mx[i]);
            }
        }
        return ans;
    }
};
