class Solution {
public:

    long long solve(vector<long long> diff){
        if(diff.size() == 0) return 0;

        long long int n = diff.size();
        long long int ans= diff[0];

        for(int i=1;i<n;i++){
            if(diff[i] > diff[i-1]) ans+=diff[i]-diff[i-1];
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int sign = 0;
        long long int ans = 0;
        vector<long long> diff;

        for(int i = 0;i < nums.size();i++){

            if(sign  == 0 && nums[i] > target[i]){
                ans += solve(diff);
                diff.clear();
                sign = 1;
            }

            if(sign == 1 && nums[i] < target[i]){
                ans += solve(diff);
                diff.clear();
                sign = 0;
            }

          diff.push_back(abs(target[i] - nums[i]));
        }
        ans += solve(diff);
    return ans;
    }
};
