class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
         int n=nums.size();
        if(n==1)
        {
            return abs(target[0]-nums[0]);
        }

        vector<int>c(n+1),d(n+1);
        for(int i=0;i<n;i++)
    {
        c[i]=target[i]-nums[i];
    }
    d[0]=c[0];
    for(int i=1;i<n;i++)
    {
       d[i]=c[i]-c[i-1];
    }
    long long ans=0;
    int f;
    int p;
    long long x=0,y=0;
    for(int i=0;i<n;i++)
    {
        if(d[i]>0)
    {
x+=d[i];
    }
    else
    {
        y-=d[i];
    }
    }
    ans=max(x,y);
    //ans+=1;
    return ans;
    }
};
