class Solution {
        public long minimumOperations(int[] nums, int[] target) {
            int n = nums.length;
            for (int i = 0; i < n; i++) {
                nums[i] -= target[i];
            }
            int[] sub = new int[n];
            sub[0] = nums[0];
            for (int i = 1; i < n; i++) {
                sub[i] = nums[i] - nums[i - 1];
            }
            long positive = 0;
            long negative = 0;
            for (int i = 0; i < n; i++) {
                if (sub[i] > 0) {
                    positive += sub[i];
                } else {
                    negative += sub[i];
                }
            } 
            return Math.min(positive, -negative) + Math.abs(positive + negative);
        }
    }
