#define ll long long
class Solution {
public:
    ll solve(vector<int> &nums){
        for(auto &i : nums) i = abs(i);
        int res = nums[0];
        for (int i = 1; i < nums.size(); ++i)
            res += max(0, nums[i] - nums[i - 1]);
        return res;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& arr) {
        ll ans = 0, n = nums.size();
        for(int i = 0; i < n; i++) arr[i] -= nums[i];
        // cout<<endl;

        for(int i = 0; i < n; i++){
            int j = i;
            vector<int> v;
            while(j < n && arr[j] >= 0) v.push_back(arr[j++]);
            if(v.empty()){
                while(j < n && arr[j] <= 0) v.push_back(arr[j++]);
            }
            i = j-1;
            ans += solve(v);
            // cout<<solve(v)<<endl;
            // for(auto &k : v) cout<<k<<" "; cout<<endl;
        }
        return ans;
    }
};
