class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        long long ans=0;
        for(int i=0;i<nums.size();++i) {
            diff.push_back((target[i]-nums[i]));
        }
        ans+=abs(diff[0]);
        bool isP=(diff[0]>=0);
        // cout<<ans<<endl;
        for(int i=1;i<diff.size();++i) {
            // cout<<ans<<endl;
            if(diff[i]<0 && isP==true) {
                isP=false;
                ans+=abs(diff[i]);
                continue;
            } else if(diff[i]>=0 && isP==false) {
                isP=true;
                ans+=abs(diff[i]);
                continue;
            }
            if(isP) {
                ans+=max(diff[i]-diff[i-1],0);
            } else {
                ans+=max(abs(diff[i])-abs(diff[i-1]),0);
            }
        }
        return ans;
    }
};
