class Solution {
    int getType(int x){
        if(x>0){
            return 1;
        }else if(x<0){
            return -1;
        }else{
            return 0;
        }
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> v;
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;++i){
            v.push_back(target[i]-nums[i]);
        }
        int highest=0;
        int type=0;
        for(int i=0;i<n;++i){
            int newType=getType(v[i]);

            if(type==newType){
                if(abs(v[i])>highest){
                    ans+=abs(abs(v[i])-highest);
                }
                highest=abs(v[i]);
            }else{
                ans+=abs(v[i]);
                highest=abs(v[i]);
            }
            //printf("%d %d aaa %d\n", ans,highest,abs(v[i]));
            type=newType;
        }
        return ans;
    }
};
